import React, { useState } from 'react'

function Updater() {

    const [car , setCar] = useState({year:2025, 
                                    company:"suzuki",
                                    model:"JEEVA"})

    const handlechange  = (e) => {
            setCar( {
                ...car,
                [e.target.value] : e.target.name
            })
    }                                  

  return (
    <div>
                     <h2>CAR DATA</h2>
                      <p>Year:  {car.year} <br/>
                         Company:  {car.company} <br />
                         Model : {car.model}</p>

             <input type='text' placeholder='Year' name='year'  value={"year"} onChange={handlechange}/>

             <input type='text' placeholder='Company' name='company' value={"company"} />

             <input type='text' placeholder='Model'  value={"model"} />           
    </div>
  )
}

export default Updater
