import React, { useState } from 'react'

function Color() {
    const [color , setColor] =useState('black');


    const handleChange = (e) => {
        setColor
    }
  return (
    <div>
        <div className='palet' 
        style={{
            backgroundColor : {color}
        }}
        ></div>
        <input type='color'
        onChange={handleChange}
        />

    </div>
  )
}

export default Color
