import React, { useState } from 'react'

function Color() {
    const [color , setColor] =useState('black');


    const handleChange = (e) => {
        console.log(e.target.value)
        setColor(e.target.value)
    }
  return (
    <div>
        <div className='palet' 
        style={{
            backgroundColor :`${color}`
        }}
        ></div>
        <input type='color'
        onChange={handleChange}
        /><br />
        <button>Apply</button>

    </div>
  )
}

export default Color
