    const [ newtask , setNewtask] = useState("")


    const handleadd = () => {
                // --> task -> insert the value (newtask)

            if(newtask.trim() !==""){
                    setTasks(t => [...t,newtask]);
            }  
                setNewtask("")

    }

    const handlechange = (e) =>{
            setNewtask(e.target.value);
            // console.log(newtask)
    }

