import React, { useState } from 'react'

function Project() {

    //Tasks..
    const [ tasks , setTasks] = useState([]);

    const [ newtask , setNewtask] = useState("")


    const handleadd = () => {
                // --> task -> insert the value (newtask)

            if(newtask.trim() !==""){
                    setTasks(t => [...t,newtask]);
            }  
                setNewtask("")

    }

    const handlechange = (e) =>{
            setNewtask(e.target.value);
            // console.log(newtask)
    }

    const handledelete = (index) => {

      const UpdatedTasks =   tasks.filter((_, i )=> i !== index);
      setTasks(UpdatedTasks)
    }

    const handleuptask = (index) => {
      if(index>0){

        const uptasks = [...tasks];
        [uptasks[index] , uptasks[index-1]] = [uptasks[index-1], uptasks[index]]

        setTasks(uptasks);

      }
    }

    const handledowntask = (index) => {

      console.log(tasks)
      if(index < tasks.length- 1){

          const downtasks = [...tasks];
          [downtasks[index+1], downtasks[index]] = [downtasks[index] , downtasks[index+1]]

          console.log(downtasks)
          setTasks(downtasks);
      }
    }



  return (
   <div className='Outer-div'>
            <h2>TO-DO LIST</h2>

            <input type='text'  placeholder="Enter Task" value={newtask} onChange={handlechange}/>

            <button onClick={handleadd}>Add</button>
            <br />

                <ol>
                    {
                        tasks.map((task, index)=>
                            <li key={index}>
                              <span>
                                {task}
                                <button onClick={()=> handledelete(index)}>Delete</button>

                                <button onClick={()=>{handleuptask(index)}}>ðŸ‘†</button>
                                <button onClick={()=>{handledowntask(index)}}>ðŸ‘‡</button>
                                </span>  

                            </li>

                        )

                    }
      {console.log(tasks)}
                </ol>

    </div>
  )
}

export default Project
